<span class="line"><span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line"><span class="token comment">// Usage</span></span>
<span class="line"><span class="token keyword">const</span> tracker <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">BudgetTracker</span><span class="token punctuation">(</span><span class="token string">'https://your-worker.workers.dev'</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
