<span class="line">        <span class="token keyword">return</span> data<span class="token punctuation">[</span><span class="token string">'data'</span><span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token string">'bills'</span><span class="token punctuation">]</span> <span class="token keyword">if</span> data<span class="token punctuation">[</span><span class="token string">'success'</span><span class="token punctuation">]</span> <span class="token keyword">else</span> <span class="token punctuation">[</span><span class="token punctuation">]</span></span>
<span class="line">    </span>
<span class="line">    <span class="token keyword">def</span> <span class="token function">add_bill</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> description<span class="token punctuation">,</span> amount<span class="token punctuation">,</span> bill_type<span class="token punctuation">,</span> category<span class="token operator">=</span><span class="token string">'other'</span><span class="token punctuation">,</span> tag<span class="token operator">=</span><span class="token string">'general'</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token triple-quoted-string string">"""Add a new bill"""</span></span>
<span class="line">        bill_data <span class="token operator">=</span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token string">'description'</span><span class="token punctuation">:</span> description<span class="token punctuation">,</span></span>
<span class="line">            <span class="token string">'type'</span><span class="token punctuation">:</span> bill_type<span class="token punctuation">,</span></span>
<span class="line">            <span class="token string">'amount'</span><span class="token punctuation">:</span> amount<span class="token punctuation">,</span></span>
<span class="line">            <span class="token string">'category'</span><span class="token punctuation">:</span> category<span class="token punctuation">,</span></span>
<span class="line">            <span class="token string">'tag'</span><span class="token punctuation">:</span> tag</span>
<span class="line">        <span class="token punctuation">}</span></span>
<span class="line">        </span>
<span class="line">        response <span class="token operator">=</span> requests<span class="token punctuation">.</span>post<span class="token punctuation">(</span></span>
<span class="line">            <span class="token string-interpolation"><span class="token string">f"</span><span class="token interpolation"><span class="token punctuation">{</span>self<span class="token punctuation">.</span>api_url<span class="token punctuation">}</span></span><span class="token string">/api/bills"</span></span><span class="token punctuation">,</span></span>
<span class="line">            json<span class="token operator">=</span>bill_data</span>
<span class="line">        <span class="token punctuation">)</span></span>
<span class="line">        </span>
<span class="line">        <span class="token keyword">return</span> response<span class="token punctuation">.</span>json<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
<span class="line">    </span>
<span class="line">    <span class="token keyword">def</span> <span class="token function">get_spending_by_category</span><span class="token punctuation">(</span>self<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
