<span class="line">        response <span class="token operator">=</span> requests<span class="token punctuation">.</span>get<span class="token punctuation">(</span><span class="token string-interpolation"><span class="token string">f"</span><span class="token interpolation"><span class="token punctuation">{</span>self<span class="token punctuation">.</span>api_url<span class="token punctuation">}</span></span><span class="token string">/api/stats/category"</span></span><span class="token punctuation">)</span></span>
